import React, { useState } from "react";
import axios from "axios";

const API_KEY = "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";
const ORG_ID = "org-62IhtDbGbFgvMGflmhrnJSEG";
const PROJECT_ID = "proj_HWuNpIqkxaALXYgzdejTVIFs";
const ASSISTANT_ID = "asst_T34faD70SYGlP1077OzqJnMd";

function App() {
  const [messages, setMessages] = useState([
    {
      role: "assistant",
      content:
        "Merhaba, Kuveyt Türk Meet in One Asistanına hoş geldiniz. Size nasıl yardımcı olabilirim?",
      time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }),
    },
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    const userMsg = {
      role: "user",
      content: input,
      time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }),
    };
    setMessages((msgs) => [...msgs, userMsg]);
    setInput("");
    setLoading(true);
    try {
      const response = await axios.post(
        "https://api.openai.com/v1/threads",
        {
          messages: [
            { role: "user", content: input }
          ],
          assistant_id: ASSISTANT_ID
        },
        {
          headers: {
            "Authorization": `Bearer ${API_KEY}`,
            "OpenAI-Organization": ORG_ID,
            "OpenAI-Project": PROJECT_ID,
            "Content-Type": "application/json"
          }
        }
      );
      const threadId = response.data.id;
      // Şimdi cevabı al
      const runRes = await axios.post(
        `https://api.openai.com/v1/threads/${threadId}/runs`,
        { assistant_id: ASSISTANT_ID },
        {
          headers: {
            "Authorization": `Bearer ${API_KEY}`,
            "OpenAI-Organization": ORG_ID,
            "OpenAI-Project": PROJECT_ID,
            "Content-Type": "application/json"
          }
        }
      );
      // Cevap tamamlanana kadar bekle
      let runStatus = runRes.data.status;
      let runId = runRes.data.id;
      let answer = "";
      while (runStatus !== "completed" && runStatus !== "failed") {
        await new Promise((r) => setTimeout(r, 1500));
        const statusRes = await axios.get(
          `https://api.openai.com/v1/threads/${threadId}/runs/${runId}`,
          {
            headers: {
              "Authorization": `Bearer ${API_KEY}`,
              "OpenAI-Organization": ORG_ID,
              "OpenAI-Project": PROJECT_ID,
              "Content-Type": "application/json"
            }
          }
        );
        runStatus = statusRes.data.status;
      }
      if (runStatus === "completed") {
        const msgRes = await axios.get(
          `https://api.openai.com/v1/threads/${threadId}/messages`,
          {
            headers: {
              "Authorization": `Bearer ${API_KEY}`,
              "OpenAI-Organization": ORG_ID,
              "OpenAI-Project": PROJECT_ID,
              "Content-Type": "application/json"
            }
          }
        );
        const assistantMsg = msgRes.data.data.find((m) => m.role === "assistant");
        answer = assistantMsg?.content[0]?.text?.value || "Bir hata oluştu.";
      } else {
        answer = "Bir hata oluştu.";
      }
      setMessages((msgs) => [
        ...msgs,
        {
          role: "assistant",
          content: answer,
          time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }),
        },
      ]);
    } catch (err) {
      setMessages((msgs) => [
        ...msgs,
        {
          role: "assistant",
          content: "Bir hata oluştu. Lütfen tekrar deneyin.",
          time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }),
        },
      ]);
    }
    setLoading(false);
  };

  return (
    <div style={{ minHeight: "100vh", background: "#f8f9f8" }}>
      <div style={{ background: "#066a5d", padding: 20, display: "flex", alignItems: "center" }}>
        <img src="./Logos/beyaz_logo.png" alt="Kuveyt Türk" style={{ height: 40, marginRight: 16 }} />
        <span style={{ color: "#fff", fontWeight: 700, fontSize: 28, letterSpacing: 1 }}>KUVEYT TÜRK</span>
      </div>
      <div style={{ display: "flex", flexDirection: "column", alignItems: "center", marginTop: 32 }}>
        <div style={{ fontWeight: 700, fontSize: 28, color: "#066a5d" }}>Meet-In-One Bot</div>
        <div style={{ color: "#bfa14a", fontWeight: 500, marginBottom: 16 }}>En iyi eşleşme için Meet-in</div>
        <div style={{ background: "#fff", borderRadius: 12, boxShadow: "0 2px 16px #0001", width: 420, maxWidth: "95vw", minHeight: 400, display: "flex", flexDirection: "column" }}>
          <div style={{ background: "#066a5d", borderTopLeftRadius: 12, borderTopRightRadius: 12, padding: 16, display: "flex", alignItems: "center" }}>
            <img src="./Logos/meetinone_logo.png" alt="MeetInOne" style={{ height: 32, marginRight: 12 }} />
            <span style={{ color: "#fff", fontWeight: 600, fontSize: 20 }}>MeetInOne</span>
          </div>
          <div style={{ flex: 1, padding: 16, overflowY: "auto", display: "flex", flexDirection: "column", gap: 12 }}>
            {messages.map((msg, i) => (
              <div key={i} style={{ alignSelf: msg.role === "user" ? "flex-end" : "flex-start", maxWidth: "80%" }}>
                <div style={{ background: msg.role === "user" ? "#e6f4f1" : "#e6ede7", color: "#222", borderRadius: 8, padding: 10, fontSize: 15 }}>{msg.content}</div>
                <div style={{ fontSize: 11, color: "#888", marginTop: 2, textAlign: msg.role === "user" ? "right" : "left" }}>{msg.time}</div>
              </div>
            ))}
            {loading && <div style={{ color: "#888", fontSize: 14 }}>Yanıt yazılıyor...</div>}
          </div>
          <form onSubmit={sendMessage} style={{ display: "flex", borderTop: "1px solid #eee", padding: 12, gap: 8 }}>
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Mesajınızı yazın..."
              style={{ flex: 1, border: "none", outline: "none", fontSize: 15, padding: 10, borderRadius: 6, background: "#f4f4f4" }}
              disabled={loading}
            />
            <button type="submit" disabled={loading || !input.trim()} style={{ background: "#066a5d", border: "none", borderRadius: 6, color: "#fff", padding: "0 18px", fontWeight: 600, fontSize: 18, cursor: loading ? "not-allowed" : "pointer" }}>
              <svg width="22" height="22" fill="none" viewBox="0 0 24 24"><path fill="#fff" d="M2.01 21 23 12 2.01 3 2 10l15 2-15 2 .01 7z"/></svg>
            </button>
          </form>
        </div>
      </div>
      <div style={{ textAlign: "center", color: "#888", fontSize: 13, marginTop: 40 }}>
        © 2025 Kuveyt Türk MeetInOne. Tüm hakları saklıdır.
      </div>
    </div>
  );
}

export default App;
